import {strict as assert} from 'assert';

function longestPalindrome(s: string): string {
    let longest = s[0];

    for (let i = 0; i < s.length; i++) {
        const partWithOneCharacterInTheMiddle = expand(s, i, i);
        if (longest.length < partWithOneCharacterInTheMiddle.length) longest = partWithOneCharacterInTheMiddle;

        const partWithTwoCharactersInTheMiddle = expand(s, i, i + 1);
        if (longest.length < partWithTwoCharactersInTheMiddle.length) longest = partWithTwoCharactersInTheMiddle;
    }

    return longest;
}

function expand(s: string, left: number, right: number): string {
    while (left >= 0 && right < s.length && s[left] === s[right]) {
        left--;
        right++;
    }

    return s.substring(left + 1, right);
}

console.time('time');
assert.equal(longestPalindrome('babad'), 'bab');
assert.equal(longestPalindrome('cbbd'), 'bb');
assert.equal(longestPalindrome('aacabdkacaa'), 'aca');
assert.equal(
    longestPalindrome(
        ''
    ),
    ''
);
console.timeEnd('time');
